import { Outlet, Navigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import { Jwt } from "jsonwebtoken";

const AdminRoutes = () => {
  const [cookies] = useCookies(null);

  //if there is an user logged in
  let auth = cookies.Username ? true : false;
  if (auth) {
    //decode the token
    const decoded = Jwt.verify(cookies.AuthToken, "statesecret");
    //if user is admin it navigate to the route requested. If not navigate to the login page
    return decoded.userType === "admin" ? <Outlet /> : <Navigate to="/login" />;
  }
};

export default AdminRoutes;
