import { Outlet, Navigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import jwt_decode from "jwt-decode";

const AdminRoutes = () => {
  const [cookies] = useCookies(null);

  //decode the token
  const decoded = jwt_decode(cookies.AuthToken);
  //if user is admin it navigate to the route requested. If not navigate to the login page
  return decoded.userType === "admin" ? <Outlet /> : <Navigate to="/home" />;
};

const PrivateRoutes = () => {
  const [cookies] = useCookies(null);
  let auth = cookies.Username ? true : false;
  return auth ? <Outlet /> : <Navigate to="/login" />;
};

export { AdminRoutes, PrivateRoutes };
