import { useEffect, useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  NavLink,
  Link,
} from "react-router-dom";
import { ThemeProvider } from "@mui/material/styles";
//import MenuIcon from "@mui/icons-material/Menu";
import AddCircleIcon from "@mui/icons-material/AddCircle";
import AccountCircleIcon from "@mui/icons-material/AccountCircle";
import KeyboardArrowDownIcon from "@mui/icons-material/KeyboardArrowDown";
import "./App.css";
import theme from "./theme.js";
import {
  Toolbar,
  AppBar,
  Menu,
  Button,
  MenuItem,
  IconButton,
  Typography,
} from "@mui/material";
import { useCookies } from "react-cookie";
import Login from "./pages/login";
import { HomePage } from "./pages/home";
import { DriverPayroll } from "./pages/driverpayroll";
import { Dispatch } from "./pages/dispatch";
import { AddClient } from "./pages/addclient";
import { Schedule } from "./pages/schedule";
import Auth from "./pages/admin/Auth";
import ResetPassword from "./pages/resetPassword";
import ResetUserPassword from "./pages/admin/ResetUserPassword";

const App = () => {
  const [cookies, setCookie, removeCookie] = useCookies(null);
  const [anchorEl, setAnchorEl] = useState(null);
  const [anchorE2, setAnchorE2] = useState(null);
  const [anchorE3, setAnchorE3] = useState(null);
  const [anchorE4, setAnchorE4] = useState(null);
  const [isAuth, setIsAuth] = useState(false);
  const [userType, setUserType] = useState("");
  const userName = cookies.Username;

  const handleClose = () => {
    setAnchorEl(null);
  };
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleCloseAccounting = () => {
    setAnchorE2(null);
  };
  const handleClickAccounting = (event) => {
    setAnchorE2(event.currentTarget);
  };

  const handleCloseUser = () => {
    setAnchorE3(null);
  };
  const handleClickUser = (event) => {
    setAnchorE3(event.currentTarget);
  };

  const handleCloseAdmin = () => {
    setAnchorE4(null);
  };
  const handleClickAdmin = (event) => {
    setAnchorE4(event.currentTarget);
  };

  // const getUserInfo = (user) => {
  //   //setAuth(user.isAuth);
  //   setUserName(user.name);
  // };

  const signOut = () => {
    removeCookie("Username");
    removeCookie("AuthToken");
    removeCookie("UserType");
    setIsAuth(false);
    window.location.href = "/";
  };

  const getData = async (username) => {
    try {
      // const response = await fetch(
      //   `http://localhost:8000/getUserType/${username}`
      // );
      const response = await fetch(`/getUserType/${username}`);
      const json = await response.json();
      setUserType(json);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    if (userName) {
      setIsAuth(true);
      getData(userName);
    }
  }, [userName]);

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <AppBar>
          <Toolbar>
            <Link to="/home">
              <Typography variant="h6" color="white">
                Phoenix Bus
              </Typography>
            </Link>
            {isAuth && (userType === "dispatch" || userType === "admin") ? (
              <IconButton
                onClick={handleClick}
                color="inherit"
                style={{ marginLeft: "auto", paddingRight: "1vh" }}
              >
                <AddCircleIcon />
              </IconButton>
            ) : null}
            <Menu
              id="simple-menu"
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleClose}
            >
              <MenuItem
                component={NavLink}
                to="/addclient"
                onClick={handleClose}
              >
                New Client
              </MenuItem>
              <MenuItem
                component={NavLink}
                to="/addreservation"
                onClick={handleClose}
              >
                New Reservation
              </MenuItem>
              <MenuItem
                component={NavLink}
                to="/addquote"
                onClick={handleClose}
              >
                New Quote
              </MenuItem>
            </Menu>
            {isAuth && (userType === "dispatch" || userType === "admin") ? (
              <Link to="/schedule">
                <Button
                  id="schedule"
                  sx={{ color: "white" }}
                  style={{ marginLeft: "5vh", marginRight: "5vh" }}
                >
                  View Schedule
                </Button>
              </Link>
            ) : null}
            {isAuth && (userType === "dispatch" || userType === "admin") ? (
              <Link to="/dispatch">
                <Button
                  id="dispatch"
                  sx={{ color: "white" }}
                  style={{ marginLeft: "5vh", marginRight: "5vh" }}
                >
                  Dispatch
                </Button>
              </Link>
            ) : null}
            {isAuth && userType === "admin" ? (
              <Button
                id="accounting"
                aria-controls={open ? "accounting-menu" : undefined}
                aria-haspopup="true"
                aria-expanded={open ? "true" : undefined}
                onClick={handleClickAccounting}
                sx={{ color: "white" }}
                style={{ marginLeft: "5vh", marginRight: "5vh" }}
                endIcon={<KeyboardArrowDownIcon />}
              >
                Accounting
              </Button>
            ) : null}
            <Menu
              id="accounting-menu"
              anchorEl={anchorE2}
              open={Boolean(anchorE2)}
              onClose={handleCloseAccounting}
            >
              <MenuItem
                component={NavLink}
                to="/driverpayroll"
                onClick={handleCloseAccounting}
              >
                Driver Payroll
              </MenuItem>
              <MenuItem
                component={NavLink}
                to="/printInvoice"
                onClick={handleCloseAccounting}
              >
                Print Invoice
              </MenuItem>
              <MenuItem
                component={NavLink}
                to="/sales"
                onClick={handleCloseAccounting}
              >
                Sales
              </MenuItem>
            </Menu>
            {isAuth && (userType === "dispatch" || userType === "admin") ? (
              <Button
                id="quotes"
                sx={{ color: "white" }}
                style={{ marginLeft: "5vh", marginRight: "5vh" }}
              >
                Quotes
              </Button>
            ) : null}
            {!isAuth ? (
              <Link to="/login">
                <Button
                  id="login"
                  sx={{ color: "white" }}
                  style={{ marginLeft: "5vh", marginRight: "5vh" }}
                >
                  Login
                </Button>
              </Link>
            ) : null}
            {isAuth && userType === "admin" ? (
              <Button
                id="adminBtn"
                aria-controls={open ? "admin-menu" : undefined}
                aria-haspopup="true"
                aria-expanded={open ? "true" : undefined}
                onClick={handleClickAdmin}
                sx={{ color: "white" }}
                style={{ marginLeft: "5vh", marginRight: "5vh" }}
                endIcon={<KeyboardArrowDownIcon />}
              >
                Admin
              </Button>
            ) : null}
            <Menu
              id="admin-menu"
              anchorEl={anchorE4}
              open={Boolean(anchorE4)}
              onClose={handleCloseAdmin}
            >
              <MenuItem
                component={NavLink}
                to="/signup"
                onClick={handleCloseAdmin}
              >
                Register user
              </MenuItem>
              <MenuItem
                component={NavLink}
                to="/resetUserPass"
                onClick={handleCloseAdmin}
              >
                Reset user password
              </MenuItem>
              <MenuItem
                component={NavLink}
                to="/removeUser"
                onClick={handleCloseAdmin}
              >
                Remove user
              </MenuItem>
            </Menu>
            {isAuth ? (
              <IconButton
                onClick={handleClickUser}
                color="inherit"
                style={{ marginLeft: "auto", paddingRight: "1vh" }}
              >
                <AccountCircleIcon />
              </IconButton>
            ) : null}
            <Menu
              id="user-menu"
              anchorEl={anchorE3}
              open={Boolean(anchorE3)}
              onClose={handleCloseUser}
            >
              <MenuItem
                component={NavLink}
                to="/resetPassword"
                onClick={handleCloseUser}
              >
                Reset Password
              </MenuItem>
              <MenuItem
                component={NavLink}
                to="/"
                onClick={() => {
                  signOut();
                  handleCloseUser();
                }}
              >
                Sign Out
              </MenuItem>
            </Menu>
          </Toolbar>
        </AppBar>
        <Routes>
          <Route path="/" element={<Login />} />
          <Route path="/home" element={<HomePage />} />
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<Auth />} />
          <Route path="/resetPassword" element={<ResetPassword />} />
          <Route path="/resetUserPass" element={<ResetUserPassword />} />

          {isAuth && userType === "admin" ? (
            <Route path="/driverpayroll" element={<DriverPayroll />} />
          ) : (
            <Route path="/home" element={<HomePage />} />
          )}

          {isAuth && (userType === "dispatch" || userType === "admin") ? (
            <Route path="/dispatch" element={<Dispatch />} />
          ) : (
            <Route path="/home" element={<HomePage />} />
          )}

          {isAuth && (userType === "dispatch" || userType === "admin") ? (
            <Route path="/addclient" element={<AddClient />} />
          ) : (
            <Route path="/home" element={<HomePage />} />
          )}

          {isAuth && (userType === "dispatch" || userType === "admin") ? (
            <Route path="/schedule" element={<Schedule />} />
          ) : (
            <Route path="/home" element={<HomePage />} />
          )}
        </Routes>
      </Router>
    </ThemeProvider>
  );
};

export default App;
